// MarketDataTable component with Bloomberg styling
function MarketDataTable({ data, type }) {
  // Customize columns based on type
  const getColumns = () => {
    if (type === 'macro') {
      return [
        { key: 'ticker', label: 'TICKER', align: 'left' },
        { key: 'price', label: 'LAST', align: 'right' },
        { key: 'change', label: 'CHG', align: 'right' },
        { key: 'changePct', label: 'CHG %', align: 'right' },
        { key: 'day', label: '1D %', align: 'right' },
        { key: 'week', label: '1W %', align: 'right' },
        { key: 'month', label: '1M %', align: 'right' },
        { key: 'ytd', label: 'YTD %', align: 'right' }
      ];
    } else if (type === 'sector') {
      return [
        { key: 'ticker', label: 'TICKER', align: 'left' },
        { key: 'name', label: 'NAME', align: 'left' },
        { key: 'price', label: 'LAST', align: 'right' },
        { key: 'change', label: 'CHG', align: 'right' },
        { key: 'changePct', label: 'CHG %', align: 'right' },
        { key: 'day', label: '1D %', align: 'right' },
        { key: 'week', label: '1W %', align: 'right' },
        { key: 'month', label: '1M %', align: 'right' }
      ];
    }

    // Default columns
    return [
      { key: 'ticker', label: 'TICKER', align: 'left' },
      { key: 'price', label: 'LAST', align: 'right' },
      { key: 'change', label: 'CHG', align: 'right' },
      { key: 'changePct', label: 'CHG %', align: 'right' }
    ];
  };

  const columns = getColumns();

  // Format number with color based on sign
  const formatNumber = (value, pct = false) => {
    if (value === undefined || value === null) return '-';

    const formatted = pct 
      ? `${value >= 0 ? '+' : ''}${value.toFixed(2)}%` 
      : `${value >= 0 ? '+' : ''}${value.toFixed(2)}`;

    const className = value > 0 ? 'positive' : value < 0 ? 'negative' : '';

    return <span className={className}>{formatted}</span>;
  };

  return (
    <div className="bloomberg-section">
      <div className="bloomberg-section-header">
        <div className="bloomberg-section-title">{type === 'macro' ? 'MACRO MARKETS' : type === 'sector' ? (type === 'asia' ? 'ASIA MARKETS' : 'US MARKETS') : 'MARKETS'}</div>
        <div className="bloomberg-section-actions">
          <span className="bloomberg-section-action"></span>
          <span className="bloomberg-section-action"></span>
        </div>
      </div>
      <div className="bloomberg-grid-container">
        <table className="bloomberg-grid">
          <thead>
            <tr>
              {columns.map(col => (
                <th key={col.key} className={col.align === 'left' ? 'left-align' : ''}>
                  {col.label}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, index) => (
              <tr key={index}>
                {columns.map(col => {
                  if (col.key === 'ticker') {
                    return <td key={col.key} className="left-align">{row[col.key]}</td>;
                  } else if (col.key === 'name') {
                    return <td key={col.key} className="left-align" style={{color: '#e0e0e0'}}>{row[col.key]}</td>;
                  } else if (col.key === 'price') {
                    return <td key={col.key}>{row[col.key]?.toFixed(2)}</td>;
                  } else if (col.key === 'changePct' || col.key === 'day' || col.key === 'week' || col.key === 'month' || col.key === 'ytd') {
                    return <td key={col.key}>{formatNumber(row[col.key], true)}</td>;
                  } else {
                    return <td key={col.key}>{formatNumber(row[col.key])}</td>;
                  }
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

// Chart component with Bloomberg styling
function Chart({ data, ticker }) {
  const chartRef = useRef(null);
  const activeInterval = '1D'; // Default interval

  useEffect(() => {
    if (data && data.length > 0 && chartRef.current) {
      const ctx = chartRef.current.getContext('2d');

      // Clear canvas
      ctx.clearRect(0, 0, chartRef.current.width, chartRef.current.height);

      // Calculate min/max for scaling
      const prices = data.map(d => d.close);
      const min = Math.min(...prices) * 0.995; // Add some padding
      const max = Math.max(...prices) * 1.005;
      const range = max - min;

      // Draw chart background
      ctx.fillStyle = '#1a1d21';
      ctx.fillRect(0, 0, chartRef.current.width, chartRef.current.height);

      // Draw grid lines
      ctx.strokeStyle = '#2d3035';
      ctx.lineWidth = 1;

      // Vertical grid lines (time)
      const timeSteps = 6;
      for (let i = 1; i < timeSteps; i++) {
        const x = (i / timeSteps) * chartRef.current.width;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, chartRef.current.height);
        ctx.stroke();
      }

      // Horizontal grid lines (price)
      const priceSteps = 5;
      for (let i = 0; i <= priceSteps; i++) {
        const y = (i / priceSteps) * chartRef.current.height;
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(chartRef.current.width, y);
        ctx.stroke();

        // Add price labels
        const price = max - (i / priceSteps) * range;
        ctx.fillStyle = '#a0a0a0';
        ctx.font = '10px SF Mono';
        ctx.textAlign = 'left';
        ctx.fillText(price.toFixed(2), 5, y - 5);
      }

      // Draw price line
      ctx.strokeStyle = '#ffcc00';
      ctx.lineWidth = 2;
      ctx.beginPath();

      data.forEach((point, i) => {
        const x = (i / (data.length - 1)) * chartRef.current.width;
        const y = ((max - point.close) / range) * chartRef.current.height;

        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();

      // Draw price area
      ctx.fillStyle = 'rgba(255, 204, 0, 0.1)';
      ctx.beginPath();

      // Start from bottom left
      ctx.moveTo(0, chartRef.current.height);

      // Draw the same line shape
      data.forEach((point, i) => {
        const x = (i / (data.length - 1)) * chartRef.current.width;
        const y = ((max - point.close) / range) * chartRef.current.height;
        ctx.lineTo(x, y);
      });

      // Complete the shape to bottom right
      ctx.lineTo(chartRef.current.width, chartRef.current.height);
      ctx.closePath();
      ctx.fill();

      // Draw data points
      ctx.fillStyle = '#ffcc00';
      const pointsToShow = 5; // Show fewer points to avoid clutter
      const step = Math.max(1, Math.floor(data.length / pointsToShow));

      for (let i = 0; i < data.length; i += step) {
        const point = data[i];
        const x = (i / (data.length - 1)) * chartRef.current.width;
        const y = ((max - point.close) / range) * chartRef.current.height;

        ctx.beginPath();
        ctx.arc(x, y, 3, 0, Math.PI * 2);
        ctx.fill();
      }

      // Always show the last point
      const lastPoint = data[data.length - 1];
      const x = chartRef.current.width;
      const y = ((max - lastPoint.close) / range) * chartRef.current.height;

      ctx.beginPath();
      ctx.arc(x, y, 4, 0, Math.PI * 2);
      ctx.fill();
    }
  }, [data, ticker]);

  // Calculate change and percentage change
  const lastPrice = data && data.length > 0 ? data[data.length - 1].close : 0;
  const firstPrice = data && data.length > 0 ? data[0].open : 0;
  const change = lastPrice - firstPrice;
  const changePct = firstPrice !== 0 ? (change / firstPrice) * 100 : 0;

  const changeClass = change >= 0 ? 'positive' : 'negative';

  return (
    <div className="bloomberg-chart">
      <div className="bloomberg-chart-header">
        <div className="bloomberg-chart-title">
          <span className="bloomberg-chart-symbol">{ticker}</span>
          <span className="bloomberg-chart-details">JPY Curncy</span>
        </div>
        <div className="bloomberg-chart-price">
          <span className="bloomberg-chart-value">{lastPrice.toFixed(2)}</span>
          <div className="bloomberg-chart-change">
            <span className={changeClass}>{change >= 0 ? '+' : ''}{change.toFixed(2)}</span>
            <span className={changeClass}>{change >= 0 ? '+' : ''}{changePct.toFixed(2)}%</span>
          </div>
        </div>
      </div>
      <div className="bloomberg-chart-toolbar">
        <div className="bloomberg-chart-intervals">
          {['1D', '1W', '1M', '3M', '6M', '1Y', 'YTD', 'ALL'].map(interval => (
            <div 
              key={interval}
              className={`bloomberg-chart-interval ${interval === activeInterval ? 'active' : ''}`}
            >
              {interval}
            </div>
          ))}
        </div>
        <div className="bloomberg-chart-actions">
          <span className="bloomberg-chart-action"></span>
          <span className="bloomberg-chart-action"></span>
          <span className="bloomberg-chart-action"></span>
        </div>
      </div>
      <div className="bloomberg-chart-canvas">
        <canvas 
          ref={chartRef} 
          width="600" 
          height="300"
          style={{ width: '100%', height: '100%' }}
        />
      </div>
    </div>
  );
}

// LogViewer component with Bloomberg styling
function LogViewer() {
  const [logs, setLogs] = useState([]);
  const [filter, setFilter] = useState({ level: '', source: '' });
  const [isLive, setIsLive] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch logs on mount
  useEffect(() => {
    fetchLogs();

    // Set up polling for live updates
    const interval = setInterval(() => {
      if (isLive) {
        fetchLogs();
      }
    }, 3000); // More frequent updates

    return () => clearInterval(interval);
  }, [isLive, filter]);

  // Fetch logs from API
  const fetchLogs = async () => {
    try {
      const queryParams = new URLSearchParams();
      if (filter.level) queryParams.append('level', filter.level);
      if (filter.source) queryParams.append('source', filter.source);
      queryParams.append('limit', '100'); // Get more logs

      const response = await fetch(`${BACKEND_URL}/logs?${queryParams.toString()}`);
      const data = await response.json();
      setLogs(data.data);
    } catch (error) {
      console.error('Error fetching logs:', error);
    }
  };

  // Log level badge with color
  const LogLevelBadge = ({ level }) => {
    const className = `log-level-badge log-level-${level.toLowerCase()}`;
    return <span className={className}>{level}</span>;
  };

  // Filter logs by search term
  const filteredLogs = logs.filter(log => {
    if (!searchTerm) return true;
    return (
      log.message.toLowerCase().includes(searchTerm.toLowerCase()) ||
      log.source.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (log.stack_trace && log.stack_trace.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  });

  return (
    <div className="log-viewer-container">
      <div className="log-viewer-header">
        <div className="log-viewer-controls">
          <div className="log-viewer-control">
            <select 
              value={filter.level} 
              onChange={e => setFilter({...filter, level: e.target.value})}
            >
              <option value="">All Levels</option>
              <option value="INFO">Info</option>
              <option value="WARNING">Warning</option>
              <option value="ERROR">Error</option>
              <option value="DEBUG">Debug</option>
            </select>
          </div>

          <div className="log-viewer-control">
            <select 
              value={filter.source} 
              onChange={e => setFilter({...filter, source: e.target.value})}
            >
              <option value="">All Sources</option>
              <option value="system">System</option>
              <option value="market_api">Market API</option>
              <option value="user_interface">UI</option>
              <option value="database">Database</option>
              <option value="authentication">Auth</option>
              <option value="data_processor">Data</option>
            </select>
          </div>

          <button 
            className={`log-viewer-button ${isLive ? 'active' : ''}`}
            onClick={() => setIsLive(!isLive)}
          >
            {isLive ? 'Live' : 'Paused'}
          </button>

          <button 
            className="log-viewer-button"
            onClick={fetchLogs}
          >
            Refresh
          </button>

          <div className="log-viewer-search">
            <span className="log-viewer-search-icon"></span>
            <input
              type="text"
              placeholder="Search logs..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="log-viewer-body">
        {filteredLogs.length === 0 ? (
          <div className="text-center py-8 text-gray-400">No logs found</div>
        ) : (
          filteredLogs.map(log => (
            <div key={log._id} className="log-entry">
              <div className="log-entry-header">
                <div className="flex items-center">
                  <LogLevelBadge level={log.level} />
                  <span className="log-entry-source">{log.source}</span>
                </div>
                <span className="log-entry-time">
                  {new Date(log.timestamp).toLocaleString()}
                </span>
              </div>
              <div className="log-entry-message">{log.message}</div>
              {log.stack_trace && (
                <div className="log-entry-details">
                  {log.stack_trace}
                </div>
              )}
              {log.additional_data && (
                <div className="log-entry-details">
                  {JSON.stringify(log.additional_data, null, 2)}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

